import React, {useState} from "react";

function platformLogo (props){
  const [isExpanded, setExpanded] = useState(false)

  function expand() {
    setExpanded(true);
  }

  function contract() {
    setExpanded(false);
  }

    return (
        <div className="platformLogo" onMouseOver={expand} style={{transform: isExpanded ? "scale(1.1)" : "scale(1.0)"}} onMouseOut={contract}>
            <img className="imageThumbnail" src={props.platform1}></img>

            {/* {props.platform1}
            {props.platform2}
            {props.platform3}
            {props.platform4} */}
        </div>
      );
}

export default platformLogo;
