import React, {useState} from "react";

function Content (props){
  const [isExpanded, setExpanded] = useState(false)

  function expand() {
    setExpanded(true);
  }

  function contract() {
    setExpanded(false);
  }

    return (
        <div className="term" onMouseOver={expand} style={{transform: isExpanded ? "scale(1.1)" : "scale(1.0)"}} onMouseOut={contract}>
            <img className="imageThumbnail" src="https://store-images.s-microsoft.com/image/apps.50159.14368348513906401.b2ccf066-701e-46bb-a741-381c88665da6.d1131c8d-d837-4026-ba2a-1012dcdb36e3"></img>
        </div>
      );
}

export default Content;
