import React, {useState} from "react";

function PlatformLogo (props){
  const [isExpanded, setExpanded] = useState(false)

  function expand() {
    setExpanded(true);
  }

  function contract() {
    setExpanded(false);
  }

    return (
        // <div onMouseOver={expand} style={{transform: isExpanded ? "scale(1.1)" : "scale(1.0)"}} onMouseOut={contract}>
        //     {props.platform1}
        // </div>
            <div className="term" onMouseOver={expand} style={{transform: isExpanded ? "scale(1.1)" : "scale(1.0)"}} onMouseOut={contract}>
                <img className="imageThumbnail" src={props.image}></img>
            </div>
      );
}

export default PlatformLogo;
