import React, {useState} from "react";

function Content (props){
  const [isExpanded, setExpanded] = useState(false)

  function expand() {
    setExpanded(true);
  }

    return (
        <div className="term">
            <span onMouseOver={expand} style={{transform: isExpanded ? "1.1" : "1"}} className="work" role="img" aria-label="Tense Biceps">
              {props.gameTitle}
            </span>
            <span>{props.name}</span>
          <dd>{props.description}</dd>
        </div>
      );
}

export default Content;
